	(2664, NULL, '<HTML>\n<BODY>\n<H1 align="center"> GRADES DES RECRUES DE LA HORDE </H1><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank05" align="left"/><BR/>\n<P align="right">Adjudant</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank04" align="left"/><BR/>\n<P align="right"> Sergent-chef</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank03" align="left"/><BR/>\n<P align="right">Sergent</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank02" align="left"/><BR/>\n<P align="right">Grunt</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank01" align="left"/><BR/>\n<P align="right">Eclaireur</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">SOLDATENR?NGE DER HORDE</H1><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank05" align="left"/><BR/>\n<P align="right">Rottenmeister</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank04" align="left"/><BR/>\n<P align="right">Schlachtrufer</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank03" align="left"/><BR/>\n<P align="right">Waffentr?ger</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank02" align="left"/><BR/>\n<P align="right">Grunzer</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank01" align="left"/><BR/>\n<P align="right">Sp?her</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">部落军官军阶</H1><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank05" align="left"/><BR/><BR/> <P align="right">一等士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank04" align="left"/><BR/><BR/> <P align="right">资深士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank03" align="left"/><BR/><BR/> <P align="right">士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank02" align="left"/><BR/><BR/> <P align="right">步兵</P><BR/><BR/', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">РЯДОВЫЕ И СЕРЖАНТСКИЕ ЗВАНИЯ ОРДЫ</H1><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank05.blp" align="left"/><BR/>\r\n<P align="right">Первый сержант</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank04.blp" align="left"/><BR/>\r\n<P align="right">Старший сержант</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank03.blp" align="left"/><BR/>\r\n<P align="right">Сержант</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank02.blp" align="left"/><BR/>\r\n<P align="right">Рубака</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank01.blp" align="left"/><BR/>\r\n<P align="right">Разведчик</P><BR/><BR/>\r\n</BODY>\r\n</HTML>'),
	(2665, NULL, '<HTML>\n<BODY>\n<H1 align="center"> GRADES D\'OFFICIER DE L\'ALLIANCE </H1><BR/>\n<P align="center">Part 1</P>\n<IMG src="Interface\\PvPRankBadges\\PvPRank14" align="left"/><BR/>\n<P align="right">Connétable</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank13" align="left"/><BR/>\n<P align="right">Grand maréchal</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank12" align="left"/><BR/>\n<P align="right">Maréchal</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank11" align="left"/><BR/>\n<P align="right">Commandant</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank10" align="left"/><BR/>\n<P align="right">Lieutenant-commandant</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">OFFIZIERSR?NGE DER ALLIANZ</H1><BR/>\n<P align="center"Teil 1</P>\n<IMG src="Interface\\PvPRankBadgesPvPRank14" align="left"/><BR/>\n<P align="right">Gro?marschall</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank13" align="left"/><BR/>\n<P align="right">Feldmarschall</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank12" align="left"/><BR/>\n<P align="right">Marschall</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank11" align="left"/><BR/>\n<P align="right">Rittmeister</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank10" align="left"/><BR/>\n<P align="right">Feldkommandant</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">联盟士兵军阶</H1><BR/><BR/> <P align="center">Part 1</P><BR/> <IMG src="InterfacePvPRankBadgesPvPRank14" align="left"/><BR/><BR/> <P align="right">总元帅</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank13" align="left"/><BR/><BR/> <P align="right">战场元帅</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank12" align="left"/><BR/><BR/> <P align="right">元帅</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank11" align="left"/><BR/><BR/>', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">ОФИЦЕРСКИЕ ЗВАНИЯ АЛЬЯНСА</H1><BR/>\r\n<P align="center">Раздел 1</P>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank14.blp" align="left"/><BR/>\r\n<P align="right">Главнокомандующий</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank13.blp" align="left"/><BR/>\r\n<P align="right">Фельдмаршал</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank12.blp" align="left"/><BR/>\r\n<P align="right">Маршал</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank11.blp" align="left"/><BR/>\r\n<P align="right">Командор</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank10.blp" align="left"/><BR/>\r\n<P align="right">Лейтенант-командор</P><BR/><BR/>\r\n</BODY>\r\n</HTML>'),
	(2676, NULL, '<HTML>\n<BODY>\n<H1 align="center">GRADES MILITAIRES DE L\'ALLIANCE</H1><BR/>\n<P align="center">OFFICIERS</P><BR/>\n<P align="center">Connétable</P>\n<P align="center">Grand Maréchal</P>\n<P align="center">Maréchal</P>\n<P align="center">Commandant</P>\n<P align="center">Lieutenant-commandant</P>\n<P align="center">Chevalier-champion</P>\n<P align="center">Chevalier-capitaine</P>\n<P align="center">Chevalier-lieutenant</P>\n<P align="center">Chevalier</P><BR/>\n<P align="center">RECRUES</P><BR/>\n<P align="center">Sergent-Major</P>\n<P align="center">Sergent-chef</P>\n<P align="center">Sergent</P>\n<P align="center">Caporal</P>\n<P align="center">Soldat</P>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">MILIT?RISCHE R?NGE DER ALLIANZ</H1>\n<BR/>\n<P align="center"OFFIZIERE</P>\n<BR/>\n<P align="center"Gro?marschall</P>\n<P align="center"Feldmarschall</P>\n<P align="center"Marschall</P>\n<P align="center"Rittmeister</P>\n<P align="center"Feldkommandant</P>\n<P align="center"Ritter der Allianz</P>\n<P align="center"Kürassier</P>\n<P align="center"Hauptmann</P>\n<P align="center"Leutnant</P>\n<BR/>\n<P align="center"SOLDATEN</P>\n<BR/>\n<P align="center"F?hnrich</P>\n<P align="center"Feldwebel</P>\n<P align="center"Landsknecht</P>\n<P align="center"Fu?knecht</P>\n<P align="center"Gefreiter</P>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">联盟军阶</H1><BR/><BR/> <P align="center">军官</P><BR/><BR/> <P align="center">总元帅</P><BR/> <P align="center">战场元帅</P><BR/> <P align="center">元帅</P><BR/> <P align="center">指挥官</P><BR/> <P align="center">少校</P><BR/> <P align="center">一等骑士</P><BR/> <P align="center">上尉骑士</P><BR/> <P align="center">中尉骑士</P><BR/> <P align="center">骑士</P><BR/><BR/> <P align="center">士兵</P><BR/><BR/> <P align="center">士官长</P><BR/> <P align="center">上士</P><BR/> <P al', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">ВОЕННЫЕ ЗВАНИЯ АЛЬЯНСА</H1><BR/>\r\n<P align="center">ОФИЦЕРЫ</P><BR/>\r\n<P align="center">Главнокомандующий</P>\r\n<P align="center">Фельдмаршал</P>\r\n<P align="center">Маршал</P>\r\n<P align="center">Командор</P>\r\n<P align="center">Лейтенант-командор</P>\r\n<P align="center">Рыцарь-защитник</P>\r\n<P align="center">Рыцарь-капитан</P>\r\n<P align="center">Рыцарь-лейтенант</P>\r\n<P align="center">Рыцарь</P><BR/>\r\n<P align="center">РЯДОВЫЕ</P><BR/>\r\n<P align="center">Сержант-майор</P>\r\n<P align="center">Мастер-сержант</P>\r\n<P align="center">Сержант</P>\r\n<P align="center">Капрал</P>\r\n<P align="center">Рядовой</P>\r\n</BODY>\r\n</HTML>'),
	(2677, NULL, '<HTML>\n<BODY>\n<H1 align="center">GRADES MILITAIRES DE LA HORDE</H1><BR/>\n<P align="center">OFFICIERS</P><BR/>\n<P align="center">Grand seigneur de guerre</P>\n<P align="center">Seigneur de guerre</P>\n<P align="center">Général</P>\n<P align="center">Lieutenant-général</P>\n<P align="center">Champion</P>\n<P align="center">Centurion</P>\n<P align="center">Légionnaire</P>\n<P align="center">Garde de sang</P>\n<P align="center">Garde de pierre</P><BR/>\n<P align="center">RECRUES</P><BR/>\n<P align="center">Adjudant</P>\n<P align="center">Sergent-chef</P>\n<P align="center">Sergent</P>\n<P align="center">Grunt</P>\n<P align="center">Eclaireur</P>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">MILIT?RISCHE R?NGE DER HORDE</H1><BR/>\n<P align="center"OFFIZIERE</P><BR/>\n<P align="center"Oberster Kriegsfürst</P>\n<P align="center"Kriegsfürst</P>\n<P align="center"Kriegsherr</P>\n<P align="center"Sturmreiter</P>\n<P align="center"Feldherr</P>\n<P align="center"Klinge der Horde</P>\n<P align="center"Zornbringer</P>\n<P align="center"Blutgardist</P>\n<P align="center"Steingardist</P><BR/>\n<P align="center"SOLDATEN</P><BR/>\n<P align="center"Rottenmeister</P>\n<P align="center"Schlachtrufer</P>\n<P align="center"Waffentr?ger</P>\n<P align="center"Grunzer</P>\n<P align="center"Sp?her</P>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">部落军阶</H1><BR/><BR/> <P align="center">OFFICERS</P><BR/><BR/> <P align="center">高阶督军</P><BR/> <P align="center">督军</P><BR/> <P align="center">将军</P><BR/> <P align="center">中将</P><BR/> <P align="center">勇士</P><BR/> <P align="center">百夫长</P><BR/> <P align="center">军团士兵</P><BR/> <P align="center">血卫士</P><BR/> <P align="center">石卫士</P><BR/><BR/> <P align="center">士兵</P><BR/><BR/> <P align="center">一等士官</P><BR/> <P align="center">资深士官</P><BR/>', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">ВОЕННЫЕ ЗВАНИЯ ОРДЫ</H1><BR/>\r\n<P align="center">ОФИЦЕРЫ</P><BR/>\r\n<P align="center">Верховный Вождь</P>\r\n<P align="center">Вождь</P>\r\n<P align="center">Генерал</P>\r\n<P align="center">Лейтенант-генерал</P>\r\n<P align="center">Защитник</P>\r\n<P align="center">Центурион</P>\r\n<P align="center">Легионер</P>\r\n<P align="center">Кровавый страж</P>\r\n<P align="center">Каменный страж</P><BR/>\r\n<P align="center">РЯДОВЫЕ</P><BR/>\r\n<P align="center">Первый сержант</P>\r\n<P align="center">Старший сержант</P>\r\n<P align="center">Сержант</P>\r\n<P align="center">Рубака</P>\r\n<P align="center">Разведчик</P>\r\n</BODY>\r\n</HTML>'),
	(2790, NULL, 'Son nom ne mourra pas.$BSon sacrifice servira toujours$Bà nous montrer la voie.$B$BLes chaînes qui autrefois$Bétouffaient notre honneur$Bne nous lient plus.', 'Sein Name wird nicht vergessen.$BSein Opfer wird uns immer$Bden richtigen Weg weisen.$B$BFesseln, die einst den Atem$Bder Ehre erstickten$Bbinden uns nicht mehr.', '他的名字不会褪色。$B他的奉献精神将$B永远为后人指明方向。$B$B曾经的枷锁再也无法$B阻止我们表达对他的尊敬。 ', NULL, NULL, NULL, 'Имя его не умрет.$BЖертва его всегда$Bнам будет указывать путь.$B$BОковы, что крепко держали$Bчесть, отвагу и смелость,$Bнас не стесняют теперь.'),
	(2791, NULL, 'Entendez-vous son cri ?$BUn hurlement de bataille pour la Horde :$BLa victoire ou la mort !$B$BN\'oublions jamais$Bsa force face à la mort.$BSon rêve, à présent réalité.', 'Kannst du seinen Schrei h?ren?$BEin Schlachtruf für die Horde:$BSieg oder Tod!$B$BWir müssen uns an seine$BSt?rke im Angesicht des Todes erinnern.$BSein Traum, ist Wirklichkeit geworden.', '你能听到他的咆哮吗？$B一声惊醒部落的怒吼：$B胜利或是阵亡！$B$B我们必须牢记$B他在死亡面前爆发的力量。$B他的梦想终成现实。 ', NULL, NULL, NULL, 'Слышишь ли ты его крик?$BКлич боевой за Орду:$BПобеда или смерть!$B$BДолжны мы вовек запомнить,$Bкак отважно презрел он смерть.$BМечты его воплотились.'),
	(2792, NULL, 'Le danger est partout !$BLes ennemis cherchent à nous$Bremettre dans les fers.$B$BQuand nous combattons, pensez à$Bcelui qui fit son devoir.$BHellscream, pour toujours !', 'Gefahren nahen!$BFeinde begehren uns$Bwieder in Fesseln zu legen.$B$BWenn wir k?mpfen, denkt an ihn$B,der tat, was getan werden musste.$BH?llschrei, für immer!', '危机四伏。$B敌人想让我们$B$B再次受缚。$B$B在战斗时，让我们$B$B怀念葛罗·地狱吼$B他完成了自己的使命。$B永远的葛罗·地狱吼！', NULL, NULL, NULL, 'Здесь опасность кругом!$BЖаждут враги заковать$Bнас обратно в оковы.$B$BПомни же в яростной битве$Bо том, кто свой выполнил долг.$BАдский Крик вечно живет!'),
	(2807, NULL, 'Jour 1$bNotre pèlerinage est terminé. Nous avons finalement rejoint Silithus, où nous devrons effectuer la prise de contact. Nous sommes venus de bien des endroits différents pour mettre nos efforts en commun, et mener à bien cette entreprise monumentale.$b$bUne extraordinaire atmosphère de fébrilité règne parmi nous. Demain, nous consacrerons toute notre énergie à la construction de ce qui nous permettra de communiquer avec EUX.$b$bDemain nous érigerons une pierre des vents !', 'Tag 1$BUnsere Pilgerreise ist zu Ende. Endlich haben wir Silithus erreicht, wo wir Kontakt aufnehmen sollen. Wir sind von vielerlei Orten gekommen, um bei dem monumentalen Unterfangen zusammenzuarbeiten.$BDie Luft knistert vor Aufregung. Morgen gilt es all unsere Energie auf den Aufbau dessen zu konzentrieren, was uns die Kommunikation mit IHNEN erm?glichen soll.$BMorgen wird ein Windstein errichtet werden!', '第1天$B 我们的旅行结束。我们终于到达希利苏斯，也就是我们要建立联系的地方。我们来自许多地方，要一起为不朽的事业通力合作。$B$B 空气中弥漫着一股兴奋。明天我们就要将所有的精力拿来建造与“他们”进行沟通的东西。$B$B 我们将在明天建造一座风石!', NULL, NULL, NULL, 'День 1\r\nНаше странствие закончено. Мы наконец-то дошли до Силитуса, где и свяжемся с ними. Мы пришли сюда из самых разных мест, чтобы сообща свершить великое дело.\r\n\r\nВоздух весь наполнен предвкушениями. Завтра мы целиком посвятим себя строительству сооружения, которое позволит нам общаться с НИМИ.\r\n\r\nЗавтра мы будем возводить ветровой камень!'),
	(2808, NULL, 'Jour 2$bL’emplacement de la Pierre des vents a été le sujet d’un grand débat. Même les plus sages d’entre nous sont incapables de déterminer avec une grande certitude l’endroit exact auquel se réfère la vieille prophétie.$b$bJe suis resté avec le groupe nord d’origine, et le temps prouvera indubitablement que nous avons raison. Soutenir que les lignes d’énergie se croisent à cet endroit selon des angles défavorables est idiot et naïf.$b$bNous avons déjà rassemblé tous les matériaux de construction qui nous sont nécessaires.', 'Tag 2$BDie Lage des Windsteins war Grund heftiger Diskussionen. Nicht einmal die Weisesten unter uns k?nnen mit bestimmter Sicherheit sagen, wo genau die Stelle, die in der alten Prophezeiung genannt wurde, ist.$BIch verbleibe bei der ursprünglichen, n?rdlichen Gruppe und die Zeit wird beweisen, dass wir ohne Zweifel richtig liegen. Das Argument, das besagt, dass sich die Energielinien an dieser Stelle in einem schlechten Winkel kreuzen, ist t?richt und naiv.$BWir tragen bereits sehr schnell die ben?tigten Aufbaumaterialien zusammen.', '第2天$B 建造风石的位置引起了极大的争议。就连我们之中最聪明的人也无法百分之百确定，古老的预言里所指的确切位置究竟是哪里。$B$B 我跟原来的北边族群站在同一阵线，而时间绝对会证明我们是对的。说能量线会贯穿这个位置的不利角度实在是愚蠢又无知。$B$B 我们早已积极地在收集建构需要的材料。', NULL, NULL, NULL, 'День 2\r\nМы долго спорили о том, где следует возводить Ветровой Камень. Даже мудрейшие из нас не могут сказать наверняка, что за место описано в древнем пророчестве.\r\n\r\nЯ примкнул к тем, кто считает, что строить надо на севере, и время несомненно докажет нашу правоту. Доводы, что энергетические линии якобы пересекаются там под плохим углом, наивны и глупы. \r\n\r\nМы уже собираем с великой поспешностью все, что нужно для строительства.'),
	(2809, NULL, 'Jour 15$bNotre dévouement a fini par porter ses fruits. Nous sommes le premier camp à avoir terminé la construction d’une pierre des vents. Si les autres imbéciles étaient restés avec nous, nous aurions achevé notre tâche en moins d’une semaine !$b$bDemain, nous commencerons les rituels qui invoqueront le baron Kazum en personne. Il a demandé d’être le premier à tester le système, et nous n’avons pas pu le contredire, car il s’emporte facilement.$b$bJe pense qu’il sera très content de nous.', 'Tag 15$BUnser Einsatz hat sich bezahlt gemacht. Wir sind das erste Lager, das einen Windstein errichtet hat. W?ren die anderen Narren an unserer Seite geblieben, dann h?tten wir das Ganze sogar in weniger als einer Woche bew?ltigt!$BMorgen werden wir mit den Ritualen beginnen, die zu der Beschw?rung von Baron Kazum selbst geh?ren. Er wollte als Erster das System testen und wir konnten ihm nicht widersprechen, da er sehr schnell erzürnt.$BIch denke, dass er mit uns ?u?erst zufrieden sein wird.', '第15天$B 我们的付出有了代价。我们是第一个完成风石建构的军营。如果其他那些笨蛋跟我们一起，我们就可以在一个星期之内完成这项工程!$B$B 明天我们会开始召唤沃库仑大人的仪式。他要求第一个来测试系统，我们无法反驳他，因为他很容易生气。$B$B 我希望他会对我们很满意。', NULL, NULL, NULL, 'День 15\r\nНаше упорство себя оправдало. Мы первыми завершили установку Ветрового Камня. Останься с нами другие глупцы, на это ушло бы меньше недели!\r\n\r\nЗавтра мы приступим к ритуалам призыва самого Барона Казума. Он потребовал, чтобы его вызвали первым, и перечить ему мы не можем, поскольку в гневе он скор на расправу.\r\n\r\nЯ надеюсь, что он будет очень нами доволен.'),
	(2854, NULL, '<HTML>\n<BODY>\n<H1 align="center">GRADES D\'OFFICIER DE LA HORDE</H1><BR/>\n<P align="center">Deuxième partie</P>\n<IMG src="Interface\\PvPRankBadges\\PvPRank09" align="left"/><BR/>\n<P align="right">Centurion</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank08" align="left"/><BR/>\n<P align="right">Légionnaire</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank07" align="left"/><BR/>\n<P align="right">Garde de sang</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank06" align="left"/><BR/>\n<P align="right">Garde de pierre</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">OFFIZIERSR?NGE DER HORDE</H1><BR/>\n<P align="center"Teil 2</P>\n<IMG src="Interface\\PvPRankBadgesPvPRank09" align="left"/><BR/>\n<P align="right">Klinge der Horde</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank08" align="left"/><BR/>\n<P align="right">Zornbringer</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank07" align="left"/><BR/>\n<P align="right">Blutgardist</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank06" align="left"/><BR/>\n<P align="right">Steingardist</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">部落士兵军阶</H1><BR/><BR/> <P align="center">第二部分</P><BR/> <IMG src="InterfacePvPRankBadgesPvPRank09" align="left"/><BR/><BR/> <P align="right">百夫长</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank08" align="left"/><BR/><BR/> <P align="right">军团士兵</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank07" align="left"/><BR/><BR/> <P align="right">血卫士</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank06" align="left"/><BR/><BR/>', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">ОФИЦЕРСКИЕ ЗВАНИЯ ОРДЫ</H1><BR/>\r\n<P align="center">Часть 2</P>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank09" align="left"/><BR/>\r\n<P align="right">Центурион</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank08" align="left"/><BR/>\r\n<P align="right">Легионер</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank07" align="left"/><BR/>\r\n<P align="right">Кровавый страж</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank06" align="left"/><BR/>\r\n<P align="right">Каменный страж</P><BR/><BR/>\r\n</BODY>\r\n</HTML>'),
	(2855, NULL, '<HTML>\n<BODY>\n<H1 align="center"> GRADES DES RECRUES DE LA HORDE </H1><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank05" align="left"/><BR/>\n<P align="right">Adjudant</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank04" align="left"/><BR/>\n<P align="right"> Sergent-chef</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank03" align="left"/><BR/>\n<P align="right">Sergent</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank02" align="left"/><BR/>\n<P align="right">Grunt</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank01" align="left"/><BR/>\n<P align="right">Eclaireur</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">SOLDATENR?NGE DER HORDE</H1><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank05" align="left"/><BR/>\n<P align="right">Rottenmeister</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank04" align="left"/><BR/>\n<P align="right">Schlachtrufer</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank03" align="left"/><BR/>\n<P align="right">Waffentr?ger</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank02" align="left"/><BR/>\n<P align="right">Grunzer</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank01" align="left"/><BR/>\n<P align="right">Sp?her</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">部落军官军阶</H1><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank05" align="left"/><BR/><BR/> <P align="right">一等士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank04" align="left"/><BR/><BR/> <P align="right">资深士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank03" align="left"/><BR/><BR/> <P align="right">士官</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank02" align="left"/><BR/><BR/> <P align="right">步兵</P><BR/><BR/', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center">РЯДОВОЙ И СЕРЖАНТСКИЙ СОСТАВ ОРДЫ</H1><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank05" align="left"/><BR/>\r\n<P align="right">Первый сержант</P>