	(2856, NULL, '<HTML>\n<BODY>\n<H1 align="center"> GRADES D\'OFFICIER DE L\'ALLIANCE </H1><BR/>\n<P align="center">Part 2</P>\n<IMG src="Interface\\PvPRankBadges\\PvPRank09" align="left"/><BR/>\n<P align="right">Chevalier-champion</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank08" align="left"/><BR/>\n<P align="right"> Chevalier-capitaine</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank07" align="left"/><BR/>\n<P align="right"> Chevalier-lieutenant</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadges\\PvPRank06" align="left"/><BR/>\n<P align="right">Chevalier</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align="center">OFFIZIERSR?NGE DER ALLIANZ</H1><BR/>\n<P align="center"Teil 2</P>\n<IMG src="Interface\\PvPRankBadgesPvPRank09" align="left"/><BR/>\n<P align="right">Ritter der Allianz</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank08" align="left"/><BR/>\n<P align="right">Kürassier</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank07" align="left"/><BR/>\n<P align="right">Hauptmann</P><BR/><BR/>\n<IMG src="Interface\\PvPRankBadgesPvPRank06" align="left"/><BR/>\n<P align="right">Leutnant</P><BR/><BR/>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align="center">联盟士兵军阶</H1><BR/><BR/> <P align="center">第二部分</P><BR/> <IMG src="InterfacePvPRankBadgesPvPRank09" align="left"/><BR/><BR/> <P align="right">一等骑士</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank08" align="left"/><BR/><BR/> <P align="right">上尉骑士</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank07" align="left"/><BR/><BR/> <P align="right">中尉骑士</P><BR/><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank06" align="left"/><BR/><BR/', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align="center"> ОФИЦЕРСКИЕ ЗВАНИЯ АЛЬЯНСА</H1><BR/>\r\n<P align="center">Раздел 2</P>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank09.blp" align="left"/><BR/>\r\n<P align="right">Рыцарь-защитник</P><BR/><BR/>\r\n<IMG src="Interface\\PvPRankBadges\\PvPRank08.blp" align="left"/><BR/>